<template>
	<view>
		<view class="registerBox">
			<view class="rb-title">账号注册</view>
			<view class="rb-col">
				<input type="number" placeholder="请输入您的手机号" @input="inputPhone"/>
			</view>
			<view class="rb-col">
				<input type="number" placeholder="请输入短信验证码" @input="inputcode"/>
				<input type="button" class="getcode" value="获取验证码" v-if="show" @click="getCode" disabled />
				<input type="button" class="getcode" :value="count+'秒后重发'" disabled v-else="!show" />
			</view>
			<view class="rb-col">
				<input type="text" password="true" placeholder="请设置您的密码" @input="inputPass"/>
			</view>
			<view class="rb-col">
				<input type="text" password="true" placeholder="请重复您的密码" @input="inputPassws"/>
			</view>
			<view class="rb-xy" :class="agree?'on':''" @click="changeAgree">我已阅读并同意<navigator url="" hover-class="none">《用户协议》</navigator>
			</view>

			<view class="rb-btn"><button type="default" form-type="submit" class="greenBtn" @click="add()">注册</button></view>
			<view class="rb-link">
				<navigator url="/pages/user/login" hover-class="none" class="fl">已有账号？<text>立即登录</text></navigator>
			</view>
		</view>
	</view>
</template>

<script>
	export default {
		data() {
			return {
				agree: true,
				show: true,
				count: '',
				timer: null,
				phone:'',
				code:'',
				pass:'',
				passws:'',
				uname:'',
				bianma:''
			}
		},
		methods: {
			inputPhone: function(event) {
				this.phone = event.target.value
			},
			inputcode: function(event) {
				this.code = event.target.value
			},
			inputPass: function(event) {
				this.pass = event.target.value
			},
			inputPassws: function(event) {
				this.passws = event.target.value
			},
			inputUname: function(event) {
				this.uname = event.target.value
			},
			inputbianma: function(event) {
				this.bianma = event.target.value
			},
			changeAgree: function(e) {
				console.log(!this.agree);
				this.agree = !this.agree
			},


			getCode() {
				var phone = this.phone;
				console.log(phone);
				const TIME_COUNT = 60;
				
					uni.request({
						url:this.websiteUrl+'api/send/send/sendMsg.html',
						data:{phone:phone,type:1},
						method:"GET",
						success: (res) => { 
							if(res.data.code==1){
								uni.showToast({
									title:res.data.msg
								})
								if (!this.timer) {
									this.count = TIME_COUNT;
									this.show = false;
									this.timer = setInterval(() => {
										if (this.count > 0 && this.count <= TIME_COUNT) {
											this.count--;
										} else {
											this.show = true;
											clearInterval(this.timer);
											this.timer = null;
										}
										
									}, 1000)}
							}else{
								uni.showModal({
									title: '温馨提示',
									content: res.data.msg,
									showCancel:false,
								})
							}
						}
					})
				
			},
			
			add:function(){
			  var agree = this.agree;
			  var phone = this.phone;
			  var code = this.code;
			  var pass = this.pass;
			  var passws = this.passws;
			  var uname = this.uname;
			  var bianma = this.bianma;
			  if(agree == false){
				  uni.showModal({
				  	title: '温馨提示',
				  	content: '请阅读用户协议',
				  	showCancel:false,
				  })
			  }else{
				uni.request({
					url:this.websiteUrl+'api/home/index/userAdd.html',
					data:{phone:phone,code:code,pass:pass,passws:passws,uname:uname,bianma:bianma},
					method:"GET",
					success: (res) => {
						if(res.data.code==1){
							uni.showToast({
								title:res.data.msg 
							})
							uni.navigateTo({
								url: '/pages/login/login'
							}); 
						}else{ 
							uni.showModal({
								title: '温馨提示',
								content: res.data.msg,
								showCancel:false,
							})
							
						}
					}
				})
			  }
			},

		}
	}
</script>

